end;

end;

method checkIfVectorExists (char(500) vectorName) returns integer;

/* declare double rcEX;    */
declare integer _vectorExists;

/* _vectorExists=0;   */
/* rcEX = st.hashTblGet(0,vectorName);  */
/* if rcEX=0 then do;    */
/*    _vectorExists=1;     */
