            rc = st.setInt('rawRecordQueue',1,_rawRecordQueueNext);
            rc = st.setInt('rawRecordQueue',2,_rawRecordQueueLast);

            replaceVector ('rawRecordQueue');
            deleteVector ('rawRecordQueue');

          end;

