method init();

assetType='train';
attributeID='trainLatitude';

maxTraversals = 10;

/* monitoring */
thisModuleName='computeModule_train_trainLatitude';
processedRecordCount = 0;
processedRecordTime = 0;
processStartTime = .;
processEndTime = .;
nextProcessTime = .;
avgProcessTime = .;
/* monitoring */

